const float PI = 3.14159265;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    float c1,c2,c3,cf,r,g,b;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 5. + sin(t)*4.; 
    vec3 col; // = vec3(sin(uv.x+t*3.),cos(uv.y+t*3.),iTime*3.);

    // Time varying pixel color
    c1 = sin(uv.x*5.+t); 

    c2 = cos(uv.y*5.+t);

    c3 = sin(5.0*(uv.x*sin(iTime / 2.0) + uv.y*cos(iTime/3.0)) + t);

    //c3 = c3*c3*cos(t/2.)*2.;

    cf = c1 + c2 + c3; 

    r=sin(cf/PI+4./4.*sin(t/2.)); 
    g=cos(cf/PI*0.3*sin(t));
    b=sin(cf/PI*12.*cos(t)); 

    //col= vec3(r,g,b);
    col= step(0.5,vec3(r,g,b));
    // Output to screen
    fragColor = vec4(col,1.0);
}
