// Fork of "wondering light" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-10-09 09:59:05

void mainImage(out vec4 O,vec2 F)
{
    O=T (F+vec2(1,0))/R)+
      T (F+vec2(0,1))/R)+
      T (F+vec2(-1,0))/R)+
      T (F+vec2(0,-1))/R);
    O=pow(100./O,1.8+O-O);
    if (mod(iTime,12.)>6.) O=sqrt(1.-O);
}

#define T1 texture(iChannel1,
void mainImage(out vec4 O,vec2 F)
{
    O=T (F-1.+2.*step(.5,T1 F/iChannelResolution[1].xy).rg))/R);
    if (length(F-h(ceil(iTime*8.))*R)<100. && sin(iTime*25.13274)>.99) O=vec4(max(O.r,100.-length(F-h(ceil(iTime*8.))*R)));
    if (mod(iTime,12.)>11.9) O-=O;
}
//iChannel1 influences the flow of the black

#define R iResolution.xy
#define T texture(iChannel0,
//Hash by Dave Hoskins from: https://www.shadertoy.com/view/4djSRW
vec2 h(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}
