// by KilatAmor
#define N normalize(uv)
#define T iTime
#define ST sin(T)
void mainImage( out vec4 O, in vec2 F )
{
    vec2 uv=(F*2.-iResolution.xy)/iResolution.y;
    vec3 col;
    float l=length(uv);
    vec2 uv1=N*sqrt(l*l*l), uv2 = N*sin(l);
    uv1.y+=sin(uv1.x*cos(uv1.x+T*.7)+T);    
    float d=length(sin(uv1+T*.8)+2.*cos(uv1+T*.43));
    d=smoothstep(.33,.36,abs(fract(d*(1.4+.3*ST)-T*1.5)-(.7+.2*ST)));
    d*=sin(d)*(.35+.8*sin(1.9-length(uv)));
    col=vec3(d+.02*sin(T*.7),d+.02*sin(T*.9),d+.02*sin(T*1.2));
    if (d==0.)
    {
        uv2.x+=0.09*sin(150.*uv.x);
        uv2.y+=0.09*sin(150.*uv.y);
        uv2.y+=cos(uv2.x*cos(uv2.x+T*.5)-T);
        d=length(cos(uv2-T*0.2)+2.*sin(uv2+T*.4));
        d=fract(1.*d*(1.5+.2*ST)-T*1.2);
        d=smoothstep(.35,.36,d-.2)+.05*sin(T*5.);
        d*=.15+.8*sin(1.9-length(uv));
        d*=d;
        col=vec3(d+.1*ST,d+.1*sin(T*1.25),d+.1*sin(T*1.5));
    }
    O = vec4(col,1.);
}
