Main {

    U = 2.3*(U-.5*R)/R.y;



    float d = 1e9;
    vec2 v = U;
    float th = -float(iFrame)*.005/2.*4.;
    v *= mat2(cos(th),sin(th),-sin(th),cos(th));
    float r = length(v);
    float s = r-1.;
    float t = atan(v.y,v.x);
    d = min(d,-s);
    d = min(d,roundBox(vec2(s,(fract(t/pi*40.+.5)-.5)*r),vec2(K2,.015*40.),.1));
    d = min(d,r+1.1);
    for (float i = 0.; i < N; i++) {
        vec4 p = A(vec2(i,0));
        vec2 v = U-p.xy;
        v *= mat2(cos(p.z),sin(p.z),-sin(p.z),cos(p.z));
        float r = length(v);
        float t = atan(v.y,v.x);

        float s = r-K1*p.w;
        d = min(d,s);

        d = min(d,roundBox(vec2(s,(fract(t/pi*p.w+.5)-.5)*r),vec2(K2,.01*p.w),.03));

        d = max(d,-length(r)+(K1-2.*K1*K2)*p.w);

        d = min(d,length(r)-.05);

        d = min(d,roundBox(vec2(r,(fract(t/pi*3.+.5)-.5)*r),vec2(2.*(K1+K1*K2)*p.w,.03),.03));

        d = max(d,-length(r)+.02);
    }
    d = max(d,r-1.1);
    Q = vec4(1.+d/fwidth(d));

}

#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U)
#define N 14.
#define pi 3.141592639
float roundBox ( vec2 p, vec2 b, float r) 
{
    vec2 h = 0.5*vec2(b.x,b.y)-r;
    p.xy = abs(p.xy);
    p.xy = p.xy-clamp(p.xy,-h,h);
    return length(p)-r;
}

#define K1 .015
#define K2 .1

#define normaliz(a) (length(a)>0.?normalize(a):vec2(0))

void impulse (vec4 p, vec4 v, vec2 pos, vec2 im, vec2 dv, inout vec4 f, float s) {
    im = dv+im;
    f.xy += .15*im * dot(normaliz(p.xy-pos),normaliz(im));
    f.z  += s*.15*dot(vec2(im.y,-im.x),normaliz(p.xy-pos));
}

Main {
    U = floor(U);
    if (U.x >= N || U.y >= 2.) discard;

    vec4 p = A(vec2(U.x,0));
    vec4 v = A(vec2(U.x,1));
    vec4 f = vec4(0,-1e-5*p.w*p.w,0,0);
    for (float i = 0.; i < N; i++) if (i != U.x) {
        vec4 p1 = A(vec2(i,0));
        vec4 v1 = A(vec2(i,1));
        if (length(p1.xy-p.xy) < (p.w+p1.w)*(K1+K1*K2)) {
            vec2 pos = (p1.xy*p1.w+p.xy*p.w)/(p1.w+p.w);
            vec2 im = normalize(p.xy-p1.xy) * (length(p1.xy-p.xy) - (p.w+p1.w)*(K1+K1*K2));
            vec2 w = p.xy-pos;
            w = vec2(-w.y,w.x);
            vec2 dv = -v.xy + w*v.z;
            vec2 w1 = p1.xy-pos;
            w1 = vec2(-w1.y,w1.x);
            vec2 dv1 = -v1.xy + w1*v1.z;
            impulse(p,v,pos,clamp(im,-.1,.1),dv1-dv,f,1.);

        }
    }
    if (length(p.xy)>1.-p.w*(K1+.5*K1*K2)) {
        vec2 pos = normaliz(p.xy);
        vec2 im = -normaliz(p.xy) * (length(p.xy) - 1. + (p.w)*(K1+K1*K2));
        vec2 w = p.xy-pos;
        w = vec2(-w.y,w.x);
        vec2 dv = v.xy + w*v.z;
        impulse(p,v,pos,im,vec2(pos.y,-pos.x)*.005-dv,f,-1.);
    }

    vec4 a = 30.*f/p.w/p.w;

    if (iMouse.z>0.) {
        vec2 m = 2.3*(iMouse.xy-.5*R)/R.y;
        m = m-p.xy;
        a.xy -= 2e-3*normalize(m)*exp(-2.*dot(m,m));
    }
    v += a - .001*v;
    p += .5*a + v;

    if (iFrame < 1) {
        p = vec4(U.x/N*1.6-.8,.6*sin(U.x),0,3.+U.x);
        v = vec4(0);
    }

    if (U.y == 0.) Q = p; else Q = v;

}

