vec4 char(vec2 p, int c) {  // print char. from https://www.shadertoy.com/view/llySRh
    vec2 dFdx = dFdx(p/16.), dFdy = dFdy(p/16.);
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
	return textureGrad( iChannel3, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx, dFdy );
} 

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,              // normalized coordinates
         U = 1.3*( u+u - R ) / R.y,
         S = sign(U);
    float l = .75;                        // half-heigh so that chain length = 4

    if (S.x<0.) U = -U;                   // --- build chain coordinate system
    if (abs(U.y) > l )                    // polar section   
       U.y -= l*sign(U.y), U = vec2( length(U), atan(U.y,U.x)/6.28  + l*S.y*S.x );
    S.x < 0. ? U.y -=  2. : 0.;   
    U = 5.*vec2(-U.y,U); 
    U.y -= .2;
                                          // --- go to digit coords
    U.x = mod( U.x + iTime +1., 20.) *.6; // 12./20.
    int i = int(floor(U))+1;              // digit id
    U.x = fract(U.x) / .6;
    U.x-=.5;
    l = char(U,48+i%10).x;                // draw digit
    if (i>9) U.x+=.4, l += char(U,48+i/10).x;
    l -= .2*smoothstep(6./R.y,-6./R.y, abs(U.y) -.07 ); // chain
//  U *=.6; U.x=mod(U.x,.2)-.1; l -= smoothstep(6./R.y,-6./R.y, length(U) -.1 );
    O = vec4(mix(.3,1.,l));

}
