float roundedBoxSDF(vec2 CenterPosition, vec2 Size, float Radius) {
    return length(max(abs(CenterPosition)-Size+Radius,0.0))-Radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float screen_size = min(iResolution.x, iResolution.y);
    vec2 size = vec2(300.0f, 300.0f)/iResolution.xy;

    // the pixel space location of the rectangle.
    vec2 location = iMouse.xy/iResolution.xy;

    // The radius of the corners (in pixels).
    float radius = 10.0f/screen_size;

    // Calculate distance to edge.   
    float distance = 0.05*screen_size*roundedBoxSDF(fragCoord.xy/iResolution.xy - location - (size/2.0f), size / 2.0f, radius);
    distance = clamp(distance, 0.0, 1.0);

    // Output to screen
    fragColor = mix(texture(iChannel0, fragCoord/iResolution.xy, 0.), vec4(0., 0., 0., 0.), distance);
}
