vec3 cow_pattern(vec2 uv)
{
    float scale = 6.0;
    float v = perlin( uv * scale, vec2(scale)) + 0.1;
    return vec3(v/fwidth(v));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = cow_pattern(uv + vec2(iTime * 0.2));

    // Output to screen
    fragColor = vec4(col,1.0);
}

//hash22 from: https://www.shadertoy.com/view/ldc3RB
vec2 hash22(vec2 p, vec2 repeat)
{
    p = mod(p, repeat);
    p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)));
    return -1.0 + 2.0 * fract(sin(p)*43758.5453123);
}
/////////

float perlin(vec2 uv, vec2 repeat) {
    vec2 i = floor(uv);
    vec2 f = fract(uv);

    //cubic hermite curve: f(x) = x*x*(3.0-2.0*x)
    //vec2 t = smoothstep(vec2(0.), vec2(1.), f);

    //quintic interpolation curve:
    vec2 t = f*f*f*(f*(f*6.-15.)+10.);

    return mix( mix(dot(hash22(i               , repeat), f                ), dot(hash22(i + vec2(1., 0.), repeat), f - vec2(1.,0.) ), t.x),
                mix(dot(hash22(i + vec2(0., 1.), repeat), f - vec2(0., 1.) ), dot(hash22(i + vec2(1., 1.), repeat), f - vec2(1.,1.) ), t.x),
                t.y);
}
