// golfy by #fabrice
#define H(p) fract(29. * sin(p + p) * sin(p).yx)
void mainImage(out vec4 o, vec2 u) {
    o.xyz = iResolution;
    u = floor(20. * (u + u - o.xy) / o.y) + .5;
    o = vec4(H(u), H(u + .5));
}



