/******************************************************************************/
// This works as a distributed grating reflector.

/******************************************************************************/
// I'm not sure how to remove the reflection of the screen boundary, 
// which seems to be unavoidable with this simulation method
/******************************************************************************/
//                    Go To Common for Controls
// Only certain frequency can effectively reflect the wave.
/******************************************************************************/


float boundaries(vec2 p,vec2 uv) {
        float left = 0.;
        float right = Xsize;
        //Ysize = 1.-Ysize;
        float top = 1.-Ysize/2.;
        float bottom = Ysize/2.;
        vec2 texCoord = (uv - vec2(left, bottom)) / vec2(right - left, top - bottom);
        vec4 boundaryColor = texture(iChannel1, texCoord);
        if (boundaryColor.r>.1){
            return 1.0;
        }else{return 0.0;}

        return 0.0;
}


const vec3 wallColor = vec3(0.000,0.024,0.341);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 p = fragCoord / iResolution.y;

    vec4 tex = texture(iChannel0, uv);
    float field = (tex.x/AMP+1.)*.5; // map (-1, 1) range into (0, 1)
	//vec3 col = mix(vec3(field), wallColor, boundaries(p,uv));
    field = .9*(field+0.05);
    vec3 a = vec3(0.660, 0.560, 0.680);
    vec3 b = vec3(0.718, 0.438, 0.720);
    vec3 c = vec3(0.520, 0.800, 0.520);
    vec3 d = vec3(-0.430, -0.397, -0.083);
    vec3 col = palette(clamp(field,0.,1.), a, b, c, d);
    col = pow(col,vec3(4.));
    col = mix(col, wallColor, boundaries(p,uv));




    fragColor = vec4(col, 1.0);    
}

float boundaries(vec2 p,vec2 uv) {
        float left = 0.;
        float right = Xsize;
        float top = 1.-Ysize/2.;
        float bottom = Ysize/2.;
        if (uv.x >= left && uv.x <= right && uv.y >= bottom && uv.y <= top) {
            vec2 texCoord = (uv - vec2(left, bottom)) / vec2(right - left, top - bottom);
            vec4 boundaryColor = texture(iChannel1, texCoord);
            if (boundaryColor.r>.1){
                return 1.0;
            }else{return 0.0;}
        }
        if (uv.x >= right && uv.x <= right+outPutLength) {
        vec2 texCoord = vec2(right+1., (uv.y - bottom) / (top - bottom));
        vec4 boundaryColor = texture(iChannel1, texCoord);
        return boundaryColor.r > .1 ? 1.0 : 0.0;  
        }
        if((uv.y<=bottom || uv.y>=top)&& uv.x<right) return 1.;
        return 0.0;
}

// Fork of "2D Wave Equation Simulation" by vug. https://shadertoy.com/view/3d3yzj
// 2024-10-10 08:59:44

// nine-point compact stencil of Laplacian operator
const float[9] stencil = float[](
  .05, .20, .05,
  .20, -1., .20,
  .05, .20, .05
);

// relative coordinates of 9x9 grid points
const vec2[9] grid = vec2[9](
    vec2(-1.0,  1.0), vec2(0.0,  1.0), vec2(1.0,  1.0),
    vec2(-1.0,  0.0), vec2(0.0,  0.0), vec2(1.0,  0.0),
    vec2(-1.0, -1.0), vec2(0.0, -1.0), vec2(1.0, -1.0)
);

vec4 sample1(sampler2D channel, vec2 uv) {
    // Boundary conditions for walls inside.
    // ellipse in the middle
    vec2 p = uv * vec2(1. * iResolution.x / iResolution.y, 1.0);
    return mix(texture(channel, uv), vec4(0.0, 0.0, 0.0, 1.), boundaries(p,uv));
}

// values of a field stored in a texture on the grid
float[9] getField(sampler2D channel, vec2 uv) {
	vec2 px = 1.0 / iResolution.xy;
    float[9] field;
    for (int i = 0; i < 9; i++) {
        vec2 r = uv + px * grid[i];
        field[i] = sample1(channel, r).x;
    }
    return field;
}

// Laplacian of a field
float laplacian(float[9] samples) {
    float sum = 0.0;
    for (int i=0; i<9; i++) {
        sum += stencil[i] * samples[i];
    }
    return sum;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 p = fragCoord / iResolution.y;
    vec3 col = vec3(0.0);

    // initial conditions
    if (iFrame == 0) {       
        fragColor = vec4(col, 1.0);
        return;
    }

    // Red channel stores current field values
    // Green channel stores previous field values (in time)
    vec2 tex = sample1(iChannel0, uv).rg;
    float u = tex.r;
    float u1 = tex.g;       

    float[9] field = getField(iChannel0, uv);

    // Determine the speed of wave propagation based on the region
    float delay = 0.; // Default speed

    float speed = C-delay;

    // Wave motion calculation with modified speed
    float val = 0.;
    //if(fragCoord.x<2.||fragCoord.x>iResolution.x-2.||fragCoord.y<2.||fragCoord.y>iResolution.y-2.)
    //{speed*=0.;}

    val = 2.0 * u - u1 + speed * dt * laplacian(field); // Include speed in the calculation
    val *= DAMP;

    // Calculate distance from mouse
    vec2 mousePos = iMouse.xy / iResolution.xy;
    mousePos = vec2(0.03/2.-0.01,0.5);
    float halfSize = 0.03; // Half the size of the square

    // Check if inside the square
    bool isInSquare = (abs(p.x - mousePos.x) <= halfSize) && (abs(p.y - mousePos.y) <= halfSize);

    // Source condition
    if(MouseControlSource)
    {
        if (isInSquare && iMouse.z>0.) {
            float t = float(iFrame);
            val = AMP * sin(FREQUENCY * t);
        }
    }
    else
    {
        if (isInSquare && iTime<10.) {
            float t = float(iFrame);
            val = AMP * sin(FREQUENCY * t);
        }
    }

    //reduce trailing when source disappear
    if(iTime>10.&&iTime<11. && p.x<0.03/2.-0.01+0.035)val*=.6;



    //if(fragCoord.x<2.||fragCoord.x>iResolution.x-2.)val*=0.;
    fragColor = vec4(val, u, laplacian(field), 1.0);
    //if(fragCoord.x<2.||fragCoord.x>iResolution.x-2.)fragColor.rgba*=0.;
}

bool MouseControlSource  = false;
// Periodic Feature
#define depth .111111
#define frequency 5.
#define PeriodicLength .5

// Simulation 
float C = 1.; // wave speed <= 2
float dt=.5;

float FREQUENCY = 0.2; // emission frequency
float DAMP = 1.; // 1.0 for infinite waves
float AMP = 2.;

float Ysize = 0.;
float Xsize = 1.;
float outPutLength = .05;

//iq's artical
float sdBox(in vec2 p, in vec2 b) {
    vec2 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}
// https://www.shadertoy.com/view/MdcfDj
float hash_Tong(uvec2 q)
{
	q *= uvec2(1597334677U, 3812015801U);
    uint n = q.x ^ q.y;
    n = n * (n ^ (n >> 15));
    return float(n) * (1.0/float(0xffffffffU));
}
float map(float value, float fromMin, float fromMax, float toMin, float toMax) {
    return toMin + (value - fromMin) * (toMax - toMin) / (fromMax - fromMin);
}
// by iq, https://iquilezles.org/articles/palettes/
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.283185*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.);
    vec2 uv = fragCoord / iResolution.xy;
    float waveguide = texture(iChannel0,uv).b;

    fragColor += vec4(vec3(waveguide),1.);
}

