// Integral of a square pulse signal is a triangle signal
// Integral of a triangle signal is a quadratic ladder function
//
// blue   : sqr = square
// red    : tri = integral(sqr)
// yellow : pri = integral(tri) = integral(integral(sqr))
//
// Developed to illustrate this article: https://iquilezles.org/articles/morecheckerfiltering

// Returns the square, triangular and quadratic-ladder signals
//
// Branchless is always nicer
//
vec3 funcs(in float x)
{
    x *= 0.5;

    float h = fract(x) - 0.5;

    float s = -sign(h);
    float t = 1.0 - 2.0 * abs(h);          // also 1.0 + 2.0*h*s
    float p = x + h * t;

    return vec3(s, t, p);
}

float sdSegment(vec2 p)
{
    p = abs(p);
    return length(vec2(p.x, min(1.0 - p.y, 0.0)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{	
    const float sca = 10.;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = sca * (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    p.y-=.3/450.*iResolution.y;
    p.y/=depth/450.*iResolution.y;
    p.x*=frequency/800.*iResolution.x;

    //part that without
    if(uv.x<1.-PeriodicLength)p.y*=10000.;
    float px = sca * 2.0 / iResolution.y;

    vec3 f = funcs(p.x);

    // background
    vec3 col = vec3(0.);

    // 将方波一边设置为白色
    if (p.y <= f.x && fragCoord.y/iResolution.y>.495) {
        col = vec3(1.0); // 将上半部分设置为白色
    } else {
        // graphs
        col = mix(col, vec3(1.000, 1.000, 1.000), 1.0 - smoothstep(0.5 * px, 1.5 * px, min(abs(p.y - f.x), length(vec2(fract(p.x + 0.5) - 0.5, min(1.0 - abs(p.y), 0.0))))));
    }

    fragColor = vec4(vec3(1.-col), 1.0);
}
