const float pi = 3.14159265359;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xx * 2. - vec2(1, iResolution.y / iResolution.x);

    float square = step(abs(uv.x), .5) * step(abs(uv.y), 0.5);
    float circle_s = step(distance(uv, vec2(0)), .5);
    float circle_m = step(distance(uv, vec2(0)), .4);
    float circle_h = step(distance(uv, vec2(0)), .25);
    float circle_marks = circle_s - step(distance(uv, vec2(0)), .4);
    float circle_marks2 = circle_s - step(distance(uv, vec2(0)), .48);
    float circle = circle_s - step(distance(uv, vec2(0)), .49);

    float time = iDate.w;
    float s = -mod(time, 60.) / 30. * pi - pi / 2.;
    float m = -time / 60. / 30. * pi - pi / 2.;
    float h = -time / 3600. / 6. * pi - pi / 2.;

    float marks = pi;

    fragColor = vec4(circle_s * step(abs(acos(dot(normalize(uv), normalize(vec2(cos(s), sin(s))))) / pi * -1. + 1.), .002));
    fragColor += vec4(circle_m * step(abs(acos(dot(normalize(uv), normalize(vec2(cos(m), sin(m))))) / pi * -1. + 1.), .005));
    fragColor += vec4(circle_h * step(abs(acos(dot(normalize(uv), normalize(vec2(cos(h), sin(h))))) / pi * -1. + 1.), .01));
    fragColor += vec4(circle_marks * step(mod(.01 + 6. * abs(acos(dot(normalize(uv), normalize(vec2(cos(marks), sin(marks))))) / pi), 1.), .02));
    fragColor += vec4(circle_marks2 * step(mod(.05 + 30. * abs(acos(dot(normalize(uv), normalize(vec2(cos(marks), sin(marks))))) / pi), 1.), .1));
    //fragColor += circle;
}
