void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    uv.x *= (iResolution.x / iResolution.y);

    uv *= 0.5;

    uv.x = abs(uv.x);

    uv = vec2(length(uv), atan(uv.y, uv.x));

    vec3 color = vec3(0.0);

    float t = iTime * 0.2;

    float d = 0.0;
    float ticks = 3.0;
    for (float cc = 0.0; cc < ticks; ++cc)
    { 

        vec3 uvz = vec3(uv, sin(cc * 2.1 + t));
        uvz.xy = vec2(cos(uvz.x + t), sin(uvz.y - t));
        float p1 = length(uvz) * cos(cc * cos(t));
        float p2 = atan(uv.y + cos(t * 0.6 + cc * 3.7), uv.x + sin(t * 0.94 + cc * cos(cc + t)));
        vec2 pv = vec2(p1, p2);

        uv.x = pv.x / uv.x;
        uv.y = pv.x * pv.y * uv.y;

        float ap1 = cos(t * cc + 0.25) + cc * 3.141529 * (2.0 / 3.0);
        float ap2 = sin(t * cc + cc * 3.4);
        float ap3 = pv.x;
        float a = length(uvz - vec3(ap1, ap2, ap3)); 
        float b = length(vec3(pv, sin(iTime + cc * 44.3)));
        float c = length(vec3(sin(iTime + cc * 1.1), pv - uv));

        d = sin(t);

        vec3 f = cross(uvz - vec3(pv, cos(cc + t * 0.2)), vec3(sin(a * d), cos(b * d), tan(c * 0.7)));
        color.r += f.x * f.x;
        color.g += f.y * f.x;
        color.b += f.z * f.z;



    }

    color /= pow(ticks, 6.0);


    fragColor = vec4(color, 1.0);


}
