#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float a,b,i,j,d=.1,e,n; vec3 p,r=iResolution;
vec2 q;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*4.;
    p.yz*=R2D(iTime);

    for(j=0.;j++<4.;){
        a=1.14;
        p.x=a-abs(p.x);
        q=vec2(p.x*cos(a)-p.y*sin(a), p.x*sin(a)+p.y*cos(a));
        d=length(q*1.6-p.zy)-1.2;
    }e+=d;
}o+=vec4(sqrt(p*p),1)*3./i;
}
