#define PI 3.14159
#define R iResolution
#define SS(a,b,c) smoothstep((a)-(b),(a)+(b),c)
mat2 Rot(float r) {
    float s = sin(r);
    float c = cos(r);
    return mat2(s,c,-c,s);
}
float dotGrid(vec2 uv, float size) {
    uv = mod(uv,2.)-1.;
    float l = length(uv);
    return SS(size,-0.01,l);
}
vec2 toPolar(vec2 uv) {
    // Convert Cartesian to Polar coords
    float r = length(uv)+iTime+length(uv);
    float theta = atan(uv.y, uv.x)+length(uv);
    if (uv.y>0.)
        return vec2(r,theta/PI/2.);
    else
        return vec2(r,(theta+PI)/PI/2.+.5);
}
float swirlPattern(vec2 uv, float arms) {
    return sin(toPolar(uv).y*PI*2.*arms);
}
float swirlPattern(vec2 uv) {return swirlPattern(uv, 5.);}
void mainImage( out vec4 C, vec2 XY ){
    float factor = R.x/1024.; // designed for 1024*576
    C=vec4(0.);
    vec2 uv = XY/R.xy;
    vec2 uvc = (XY-R.xy/2.)/R.y;
    // stacking the swirl pattern a bunch by feel
    C.b = swirlPattern(uvc,3.);
    C.b += swirlPattern(Rot(iTime*.5)*uvc*(1.5+.1*sin(iTime)),3.);
    C.b *= .2*swirlPattern(Rot(iTime*.25+2.+length(uvc*5.))*uvc*(2.+.1*sin(iTime*.5)));
    C.b += swirlPattern(uvc);
    C.b += min(C.b,1.+swirlPattern(length(uvc)+uvc*Rot(iTime*.5)));
    C.b = min(C.b,1.+swirlPattern(uvc*3.*Rot(iTime*.25),3.));
    C.b -= swirlPattern(uvc*Rot(-iTime*.25),8.);
    // normalize
    C.b += 2.;
    C.b *= .25;
    // convert to dots. weird dither thing
    C.r = dotGrid(uvc*80.*factor, C.b*.8); // 0 to 1.41-ish
    C.g = dotGrid(uvc*160.*factor, C.b*.8);
    C.b = dotGrid(uvc*40.*factor, C.b*.9);
    //C.rgb = vec3(C.r);
    // C.rgb = vec3(C.r);
    C.a = 1.; // usually ignored anyway
}
