#define time iTime
#define res iResolution

vec3 palette(float t)
{
    vec3 a = vec3(0.9,0.3,0.9);
    vec3 b = vec3(2., 2., 2.);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263, 0.416, 0.557);
    return a + b * cos(6.28318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - res.xy) / res.y;
    vec2 st = vec2(sqrt(dot(uv,uv)), atan(uv.y,uv.x));

    // polar coords
    float r = st.x;
    float a = st.y;

    r *= .5+.5*sin(time);
    a /= 3.14;

    vec3 col = .1 * (3.1415/4.) * palette(r + a + time);
    col = smoothstep(.1, .12, col);
    col = pow(col, vec3(.02));

    fragColor = vec4(col, 1.);
}
