void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float PI = 3.1415926;

    //float m = 1.0 - sin(uv.x * PI) * 0.5;
    //uv.x *= m;

    float v = sin(uv.x * PI * 8.0 - PI * 0.5);
    v = step(0.9, v);

    float sides = sin(uv.x * PI);
    sides = step(0.5, sides);

    float h = sin(uv.y * PI * 6.0 - PI * 0.5);
    h = step(h, 0.015);
    v = step(sides, h) * v;



    fragColor = vec4(vec3(v), 1.0);
}
