#define rotation(angle) mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

float PI = 3.14159;
float TAU = 2.*3.14159;

// Arc SDF from IQ
float sdRing( in vec2 p, in vec2 n, in float r, in float th )
{
    p.x = abs(p.x);
    p = mat2(n.x,n.y,-n.y,n.x)*p;
    return max( abs(length(p)-r)-th*0.5,
                length(vec2(p.x,max(0.0,abs(r-p.y)-th*0.5)))*sign(p.x) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = ( fragCoord - .5* iResolution.xy ) /iResolution.y;  
   vec3 col = vec3(0);
   float t = fract(.2*iTime);
   uv *= 16.;
   float r = length(uv);
   float rID = round(r);
   float spinFactor = round(3.*sin(rID/2.));
   uv *= rotation(spinFactor*TAU*(-t+rID/8.));
   float arc = sdRing(uv,vec2(sin(PI),cos(PI)), rID, .4+.35*sin(TAU*t+rID/2.));  
   col += smoothstep(.06,-.06,arc);
   fragColor = vec4(col,1.0);
}
