float hash(float n)
{
   return fract(sin(n)*43758.54554213);
}
vec3 RandVec(in vec2 fragCoord)
{
   vec3 idk;
   idk.x = hash(fragCoord.x/fragCoord.y*iTime);
   idk.y = hash(idk.x*iFrameRate*42849233.1847242);
   idk.z = hash(dot(idk.xy,vec2(idk.x/iTimeDelta*2948202.2418573,idk.y)));
   return idk.yxz;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;
    col = RandVec(fragCoord-iResolution.xy/2.0);
    fragColor = vec4(col,1.0);
}
