void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0; 
    uv.x *= iResolution.x / iResolution.y;

    // Normalize the sine wave value to range between 0 and 1
    float normalizedSin =  abs(sin(iTime * 1.4)) / 1.1;

    // Use smoothstep to smooth out the transitions of the sine wave
    float smoothSin = smoothstep(0.0, 1.0, normalizedSin);

    // Apply the smoothSin value to affect the color transitions
    float timeEffect = uv.y + smoothSin; 
    float timeEffect2 = -uv.y + smoothSin; 

    // Output to screen
    fragColor = vec4(timeEffect2, timeEffect, timeEffect, 1.0);
}
