#define tr smoothstep(-.02, .02, abs(fract(iTime*.04) - .5) - .25) 
#define pi acos(-1.)
#define TAU (2.*pi)

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy; r -= mod(r, 4.);
    u = (u + u - r) / r.y;
    float len = length(u);

    u = mix(
           vec2(
               fract(atan(u.y, u.x) / TAU + iTime / 18.) * 6.,
               log(length(u)) + .5
           ), u, tr) * 8.;

    float t = fract(iTime / 2.),
          y = floor(u.y + t),
          T = 8. - floor(sin(.628 * floor(u.x)) * 1.2 + 5.),
          B =      floor(sin(.314 * floor(u.x)) * 1.3 + 5.) - 5.;


    u.y = fract(u.y + t);
    if(B > y || y > T )
        u.y += y - mix(T, B, t);

    o = vec4(
               + .5 
               - length(
                     mod(u, vec2(1, T - B + 1.)) - .5
                 )

             ) / fwidth(len) * .1;
}
