#define DOTS 300
#define PHI 0.618033989
#define TAU 6.28318531




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy/2.) / length(iResolution.xy) * 2.;
    float arad = sqrt(1./float(DOTS));

    float dd = 10.;
    vec2 md;
    float j,theta;

    for(int i=0;i<DOTS;i++){
        j = float(i) - fract(iTime * TAU / 300.);
        theta = j*PHI*TAU + length(uv);
        md = vec2(sin(theta),cos(theta))*sqrt(j/float(DOTS));
        dd = min(length(uv-md),dd);

    }

    for(int i=0;i<DOTS;i++){
        j = float(i) - fract(iTime * TAU / 300.);
        theta = -j*PHI*TAU - length(uv);
        md = vec2(sin(theta),cos(theta))*sqrt(j/float(DOTS));
        dd = min(length(uv-md),dd);

    }

    float tval = cos(iTime*TAU / 20.)*3.+5.;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //float v = pow(1.-dd,tval*tval+1.) * length(uv) * (1.-length(uv)) * 4.;

    float aluv = max(0.,length(uv) - arad) / (1.-arad);
    float donut = aluv * (1.-aluv) * 4.;
    float v = ( 1. - dd/arad ) * donut * donut;
    float core = min(1.,length(uv)/arad);

    float negv = ( max(0.,-v) * core ) / arad / 2.;

    v = max(0.,v);

    negv +=  core*core  - 2.*core + 1.;

    //v = negv / arad / 2.;

    vec3 col = vec3(pow(v,0.5),pow(v,0.9),pow(v,2.5));

    col += vec3(pow(negv,2.5),pow(negv,0.9),pow(negv,0.5));


    // Output to screen
    fragColor = vec4(col,1.0);
}
