void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r,g,b,s,f,z;
    float x=fragCoord.x/iResolution.x-0.5;
    float y=fragCoord.y/iResolution.y-0.5;
    z=(0.5-y*5.)/5.;
    y/=z;
    x/=z;

    s = sin(iTime+y*20.)/2.;
    f = sin(iTime+y*37.)/30.;
    g = 0.0010/((x*x)/(1.-sin(iTime/5.)+s));
    b = 0.15/((x*x)/(f/15.))-g;
    r = float((x-f)/(x-s)*z-s)*(s*f*13.);
    r+= float((x+f)/(x+s)*z+s)*(s*f*23.);
    r*=float(z>0.);
    g*=float(z>0.);
    b*=float(z>0.);

    // Output to screen
    fragColor = vec4(r/2.,g,b,1.0);
}
