vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.253,0.326,0.557);

    return a + b * cos( 2.28318 * (c * t + d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    // Chromatic aberration offsets for each channel
    float aberrationStrength = 0.005; // Adjust strength as needed
    vec2 redUV = uv0 + aberrationStrength * vec2(0.55, 0.6);  // Slight offset for red
    vec2 greenUV = uv0;                                         // No offset for green
    vec2 blueUV = uv0 - aberrationStrength * vec2(0.25, 0.22);  // Slight offset for blue

    // Iteration for complexity .523 Starting //
    for (float i = 0.0; i < 2.0; i++) {
        // Distortion and fractal UV modification ( Higher # more complex 3 looks good too )
        uv = fract(uv * 6.723) - .5;
        float d = length(uv) * exp(-length(uv0));

        // Compute color for each channel
        vec3 redCol = palette(length(redUV) + i * .4 + iTime * .4);
        vec3 greenCol = palette(length(greenUV) + i * .4 + iTime * .4);
        vec3 blueCol = palette(length(blueUV) + i * .4 + iTime * .4);

        d = sin(d * 12. + iTime) / 12.;
        d = abs(d);
        d = pow(0.01 / d, 0.595);

        // Combine the separate color channels
        finalColor.r += redCol.r * d;
        finalColor.g += greenCol.g * d;
        finalColor.b += blueCol.b * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
