renderpass.code:
vec4 circle(vec2 uv, float r, vec3 color, vec2 offset) {
    uv += offset;

    float d = length(uv * r);
    //d = step(.2, d);
    d = smoothstep(.3, 1., d);

    vec4 col = 1. - vec4(d);
    col *= vec4(color, 2.);

    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;


    vec4 color = vec4(0.);

    float t = iTime / 2.;

    vec4 c1 = circle(uv, 2., vec3(0., 1., 1.), vec2(sin(t - .5), 0.));
    vec4 c2 = circle(uv, 2., vec3(1., 0., 1.), vec2(sin(t), 0.));
    vec4 c3 = circle(uv, 2., vec3(1., 1., 0.), vec2(sin(t + .5), 0.));

    color = mix(color, c1, c1.a);
    color = mix(color, c2, c2.a);
    color = mix(color, c3, c3.a);

    //color = c1 + c2 + c3;

    // Output to screen
    fragColor = color;
}
