void mainImage(out vec4 color, in vec2 coord) {

    vec2 uv = coord / iResolution.y;
    color=vec4(1.);
    float wx=0.;
    float sx=3.;
    float d=2.;
    vec2 uv2=6.6*coord/iResolution.xy;
    float n=30.;

    float v=0.05;
    vec4 color2=vec4(1.);

    if(uv2.x>0.2 && uv2.x<=6.4){
    float c=mod(float(iFrame)/240.,0.1)+0.15;
    float check=0.;
    for(float o1=v;o1<=2.5;o1+=v){
    float o2=o1+v/2.;

    if(cos(6.*(c+o1))+20.*(6.*(c+o1)/(uv2.x+sx)-1.)<=-uv2.y && cos(6.*(c+o2))+20.*((6.*(c+o2))/(uv2.x+sx)-1.)>=-uv2.y){
    check=1.;
    }
    }


    for(float o1=v*1.5;o1<=1.5;o1+=v){
    float o2=o1+v/2.;

    if(cos(6.*(c+o1))+20.*(6.*(c+o1)/(uv2.x+sx)-1.)<=-uv2.y && cos(6.*(c+o2))+20.*((6.*(c+o2))/(uv2.x+sx)-1.)>=-uv2.y){
    check=2.;
    }
    }
    for(float b2=1.1;b2>=0.6;b2-=(0.0001)){ 
    if(abs(uv2.y-wx-sin(d*b2*(uv2.x+sx))-20.*(1.-b2))<=0.002 && check==1.){
    color2=vec4(pow(0.5+0.5*cos(300.*b2),1.5))*vec4(0.5+0.5*cos(n*b2),0.5+0.5*sin(n*b2),0.5-0.5*cos(n*b2),1.);
    }

    if(abs(uv2.y-wx-sin(d*b2*(uv2.x+sx))-20.*(1.-b2))<=0.002 && check==2.){
    color2=vec4(pow(0.5-0.5*cos(300.*b2),1.5))*vec4(0.5+0.5*cos(n*b2),0.5+0.5*sin(n*b2),0.5-0.5*cos(n*b2),1.);;
    }

    }


    float ax2=0.5+0.5*cos(5.*(uv2.x-3.3));
    float ay2=0.5+0.5*cos(5.*(uv2.y*iResolution.y/iResolution.x-1.85));
    color*=2.*color2*vec4(ax2*ay2);


    }else{
    color=vec4(0.);
    }
    }
