void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    vec2 pole = iMouse.xy/iResolution.xy;

    if(dot(pole, pole) == 0.) pole = vec2(0.5);

    uv -= pole;

    float invFact = 0.5 - 0.5*cos(0.5*iTime);
    uv /= mix(1., dot(uv, uv), invFact);

    uv += pole;

    fragColor = texture(iChannel0, uv);
}
