// Rainbow Superstar by Kristian Sivonen (ruojake)
// CC BY-SA 4.0 (https://creativecommons.org/licenses/by-sa/4.0/)

mat2 rot(float a)
{
    float s = sin(a);
    float c = cos(a);

    return mat2(c, -s, s, c);
}

// https://iquilezles.org/articles/distfunctions2d/
// https://www.shadertoy.com/view/3tSGDy
float sdStar5(in vec2 p, in float r, in float rf)
{
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
    float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
    return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}

float img(vec2 p, float t)
{
    p += sin(vec2(.5 * t, t)) * .5;
    p *= rot(t * 4.);
    return 1. - sdStar5(p, .25, .5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    float t = iTime;
    vec2 p = uv;

    for(int i = 0; i < 8; ++i)
    {
        t += 5. * img(p, t) / float(i+7);
    }

    fragColor = vec4(cos((t - iTime) * vec3(3.11, 2., -.412)) * .5 + .5,1.0) + clamp(exp((iTime - t)), 0., 1.);
}
