void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 id_col = floor((uv*10.0)+10.0);
#define CDEAD vec3(.1)+vec3(id_col.x*0.0425,0.1,id_col.y*0.0325)
#define CALIVE vec3(.5,1,1)

    fragColor = vec4(texture(iChannel0,uv).r>0.5?CALIVE:CDEAD,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0);
    if (1.5 > iTime)
    {
        float r0 = texture(iChannel1, uv).r;
        float r1 = texture(iChannel2, uv).r;
        float r2 = texture(iChannel3, uv).r;
        col.r = r0 < 0.5 || r1 > 0.8 || r2 > 0.8 ? 1.0 : 0.0;
    }
    else
    {
        int nr = 0;
        for (float i = -1.0; i <= 1.0; i += 1.0)
        for (float j = -1.0; j <= 1.0; j += 1.0)
        {
            float value = texture(iChannel0, uv+(vec2(i,j)/iResolution.xy)).r;
            nr += value > 0.0 ? 1 : 0;
        }

        bool alive = texture(iChannel0, uv).r > 0.0;

        if (alive)
        {
            if (2 > nr || 3 < nr)
                col.r = 0.0;
            if (3 == nr || 4 == nr)
                col.r = 1.0;
        }
        else
        {
            if (3 == nr)
                col.r = 1.0;
        }
    }

    fragColor = vec4(col,1.0);
}
