void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float wave = (sin(uv.x * 12.762 + iTime * 2.0) + sin(uv.x * 19.28 + iTime * 1.3)) * 0.2 + 0.5;

    float col = smoothstep(wave - 0.1, wave + 0.1, uv.y);

    vec3 col3 = (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4))) * col;
    col3 += (0.5 + 0.5*sin(iTime+uv.xyx+vec3(0,2,4))) * (1.0 - col);

    fragColor = vec4(col3, 1.0);
}
