float valueNoiseMix = 0.4;
float noiseColSpeed = 9.;
float displaceSpeed = 5.;
float displacementRadius = 1. / 60.;

float noise(float seed, float tscale) { //random function i made up to generate noise
    float a = sin(364.437 * seed + 521.194);
    float b = tan(623.567 * a - 892.582);
    float c = cos(196.923 * b + iTime * tscale);
    return c;
}

float id(vec2 point) { //pixel id divided by total num pixels on the screen
    return (point.x + point.y * iResolution.x) / (iResolution.x * iResolution.y);
}

vec2 rand2d(vec2 seed) { //generates a random 2d vector to be used as the displacement
    return(vec2(noise(seed.x - seed.y, displaceSpeed), noise(seed.x + seed.y + 14.563, displaceSpeed)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv + (rand2d(uv) * displacementRadius)).rgb;
    vec3 noiseCol = vec3(noise(id(fragCoord), noiseColSpeed));
    col = mix(col, col * noiseCol, valueNoiseMix);
    // Output to screen
    fragColor = vec4(col,1.0);
}
