#define ORBIT_TRAPS 1
const vec2 offset = vec2(1.678, -0.05425);

#if ORBIT_TRAPS
float dist = 1e10;
float trap(vec2 pos)
{
    return sqrt(abs(cos(pos.x * pos.y)));
}
#endif

vec2 cpow(vec2 base, vec2 exponent)
{
    float absBase = length(base);
    float argBase = atan(base.y, base.x);
    float lnAbs = log(absBase);
    float theta = exponent.x * argBase + exponent.y * lnAbs;
    return vec2(cos(theta), sin(theta)) * exp(exponent.x * lnAbs - exponent.y * argBase);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int maxI = 1000;
    float scale = 1. / (exp((iTime*iTime / (iTime + 32.))) - 0.85);
    vec2 c = offset + scale * (2. * fragCoord - iResolution.xy)/iResolution.y;
    vec2 z = c;
    float val;
    for(int i; i < maxI; i++)
    {
        #if ORBIT_TRAPS
        dist = min(dist, trap(z));
        #endif
        z = cpow(c, z);
    }
    #if ORBIT_TRAPS
    fragColor = vec4(dist);
    #else
    fragColor = vec4(atan(z.y, z.x), length(z), 0., 0.);
    #endif
}
