vec3 fromLinear(vec3 linearRgb) {
    bvec3 cutoff = lessThan(linearRgb, vec3(0.0031308));
    vec3 higher = vec3(1.055) * pow(linearRgb, vec3(1.0 / 2.4)) - vec3(0.055);
    vec3 lower = linearRgb * vec3(12.92);

    return mix(higher, lower, cutoff);
}

vec3 colorize(float u) {
    return clamp(vec3(u, 0, -u), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 u = texelFetch(iChannel0, ivec2(gl_FragCoord.xy), 0);
    fragColor = vec4(fromLinear(colorize(u.r / u.a)), 1.0);
}

void rnd_init() {
    rnd_seed = hash2(uvec3(gl_FragCoord.xy, iFrame));
}

vec3 s(vec2 p, vec2 c, vec2 xy) {
    xy -= c;
    float r2 = dot(xy, xy);
    float r = sqrt(r2);
    float att = 1.0 / sqrt(r); // wave propagates in 2D space only, so its power (amplitude squared) should be 1/r

    float thin_lens_phase_shift = -pow(abs(c.y-0.5), 2.0) / 0.45;

    float u = sin(p.x*(r + p.y + thin_lens_phase_shift)) * att;
    return vec3(isnan(u) ? 0.0 : u, isnan(att) || isinf(att) ? 0.0 : att, r);
}

const float freq = 4e3;

float itg(vec2 xy, float time) {
    float slitSize = 2e-1;
    vec2 slit = vec2(0, (rnd_uniform() - 0.5) * slitSize + 0.5);
    vec3 source = s(vec2(freq, time), vec2(-0.5, 0.5), slit);
    return source.y * (
        s(vec2(freq, source.z), slit, xy).r
        - s(vec2(freq, source.z + 1e-5), slit - 1e-5 * normalize(slit - vec2(-0.5, 0.5)), xy).r // dipole source correction
    ) * slitSize * 2e2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    rnd_init();
    vec4 prev = texelFetch(iChannel0, ivec2(gl_FragCoord.xy), 0);

    for (int i = 0; i < 10; i++) {
        vec2 xy = (gl_FragCoord.xy + rnd_normal()) / iResolution.y;
        xy.x -= 0.1 * iResolution.x / iResolution.y;
        float time = rnd_uniform();

        prev.r += itg(xy, time) * itg(xy, time); // unbaisedly estimating wave energy at given point over time
        prev.a += 1.0;
    }

    fragColor = prev;
}

// http://www.burtleburtle.net/bob/c/lookup3.c
#define rot(x,k) (((x)<<(k)) | ((x)>>(32-(k))))

#define final(a,b,c) \
{ \
    c ^= b; c -= rot(b,14); \
    a ^= c; a -= rot(c,11); \
    b ^= a; b -= rot(a,25); \
    c ^= b; c -= rot(b,16); \
    a ^= c; a -= rot(c,4);  \
    b ^= a; b -= rot(a,14); \
    c ^= b; c -= rot(b,24); \
}

uint hash2(uvec3 v) {
    uvec3 i = uvec3(0xdeadbeefu + (2u<<2u) + v.z);

    i.xy += v.xy;
    final(i.x, i.y, i.z);
    return i.z;
}
uint hash0(uint initval) {
    uvec3 i = uvec3(0xdeadbeefu + (0u<<2u) + initval);

    final(i.x, i.y, i.z);
    return i.z;
}
// http://www.burtleburtle.net/bob/c/lookup3.c

// Construct a float with half-open range [0:1] using low 23 bits.
// All zeroes yields 0.0, all ones yields the next smallest representable value below 1.0.
float floatConstruct( uint m ) {
    const uint ieeeMantissa = 0x007FFFFFu; // binary32 mantissa bitmask
    const uint ieeeOne      = 0x3F800000u; // 1.0 in IEEE binary32

    m &= ieeeMantissa;                     // Keep only mantissa bits (fractional part)
    m |= ieeeOne;                          // Add fractional part to 1.0

    float  f = uintBitsToFloat( m );       // Range [1:2]
    return f - 1.0;                        // Range [0:1]
}

uint rnd_seed;

float rnd_uniform() {
    rnd_seed = hash0(rnd_seed);
    return floatConstruct(rnd_seed);
}
#define PI 3.1415926535897932384626433
vec2 rnd_normal() {
    float phi = 2.0 * PI * rnd_uniform();
    return sqrt(-2.0 * log(rnd_uniform())) * vec2(cos(phi), sin(phi));
}
