float sdOrientedBox( in vec2 p, in vec2 a, in vec2 b, float th )
{
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = (p-(a+b)*0.5);
          q = mat2(d.x,-d.y,d.y,d.x)*q;
          q = abs(q)-vec2(l,th)*0.5;
    return length(max(q,0.0)) + min(max(q.x,q.y),0.0);    
}

vec2 rotate(vec2 samplePosition, float rotation){
    const float PI = 3.14159;
    float angle = rotation * PI * 2.0 * -1.0;
    float cosine = cos(angle);
    float sine = sin(angle);
    return vec2(cosine * samplePosition.x + sine * samplePosition.y, cosine * samplePosition.y - sine * samplePosition.x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ar = iResolution.x / iResolution.y;

    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = fragCoord/iResolution.xy ;
    uv2.x *= ar;
    uv2.x -= 0.25 * ar;

    uv = rotate(uv,-0.12);
    uv.x += iTime/100.0;

    uv.y += sin((uv.x * 15.0) + iTime) / 10.0;

    float v = 0.0;
    v = sin(uv.y  * 200.0) * 0.5 + 0.5;
    v = smoothstep(0.5, 0.7,v);

    float b = sdOrientedBox(uv2, vec2(0.35,0.4), vec2(0.1, 0.1), 0.15);
    b = 1.0-smoothstep(0.0 ,0.002,b);

    v = max(b, v);

    // Output to screen
    fragColor = vec4(vec3(v),1.0);
}
