Main

    Q = .8*C(U);
    float N = 30.;
    vec4 H = hash(vec4(U,iFrame,1));
    for (float i = 0.; i < N; i++) {

        float a = (i+H.z);
        vec2 h = 5.*sqrt(i/N)*vec2(cos(a),sin(a));
        vec4 b = A(U+h.xy);
        vec4 n = A(U+h.xy+vec2(0,1));
        vec4 e = A(U+h.xy+vec2(1,0));
        vec3 k = .5*(.5+.5*sin(0.+2.*b.w+vec3(1,2,3)));
        b.zw = vec2(e.w-b.w,n.w-b.w);
        b.y = .5;
        b.zw = -reflect(vec3(0,0,1),normalize(vec3(b.zw,.1))).xy;
        h.xy -= b.zw-vec2(4,0);
        Q.z += k.z*exp(-dot(h.xy,h.xy)*b.y*b.y)*b.y;
        h.xy -= .2*b.zw;
        Q.y += k.y*exp(-dot(h.xy,h.xy)*b.y*b.y)*b.y;
        h.xy -= .2*b.zw;
        Q.x += k.x*exp(-dot(h.xy,h.xy)*b.y*b.y)*b.y;


    }
}

vec2 R;
#define Main void mainImage(out vec4 Q, in vec2 U) {R = iResolution.xy;
#define A(u) texture(iChannel0,(u)/R)
#define B(u) texture(iChannel1,(u)/R)
#define C(u) texture(iChannel2,(u)/R)
#define D(u) texture(iChannel3,(u)/R)



 #define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))

 vec4 hash(vec4 p4)
{
    p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

Main 
    Q = vec4(0);
        vec2 V = vec2(0);
        for (float x = -3.;x <= 3.; x++)
        for (float y = -3.;y <= 3.; y++) {

            vec2 u = vec2(x,y);

            vec4 q = A(U+u);
            vec2 v = unpackSnorm2x16(uint(q.z));
            float k = 1.7;
            vec4 o = clamp(v.xyxy+q.xyxy+u.xyxy+k*vec4(-.5,-.5,.5,.5),-.5,.5);
            float w = (o.z-o.x)*(o.w-o.y)/k/k;
            v.xy = 0.5*(o.xy+o.zw);
            V.xy += v.xy*q.w*w;
            Q.xy += q.xy*q.w*w;
            Q.w += q.w*w;
            u = u+v.xy;
             float l = length(u);
             if (l>0.) {
                 float r = exp(-.2*dot(u,u))/length(u);
                Q.xy  -= .01*q.w*(q.w-.6)*u*r;
             }
        }
        if (Q.w>0.) {
            Q.xy/=Q.w;
            V /= Q.w;
        }
        if(U.y < 20.) {
            float s = texture(iChannel1,vec2(abs(U.x-.5*R.x),0)/R).x;
            Q.yw = 20.*abs(U.x/R.x-.5)*vec2(1,1)*(s*s*s);
            Q.x = 0.;

        }
        if (length(Q.xy)>3.) Q.xy = 3.*normalize(Q.xy);
        if(U.y < 1.||U.x<1.||R.x-U.x<1.||R.y-U.y<1.) Q *= 0.;
        Q.z = float(packSnorm2x16(V.xy));

}

