void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float myuvx = 1.-uv.x;


    uv -= 0.5;
    float someoffset = fract( (1. - length(uv.y * myuvx * 10.) ));
    float xd = length(uv.y* 5.) ;
    float oneMinus = (1. - xd) * 2.;

    vec3 pink = vec3(1.0, 0.752941, 0.796078);
    vec3 purple =  vec3(0.501961, 0.0, 0.501961);


    // Output to 
    fragColor = vec4(vec3( (oneMinus + someoffset) * vec3(mix(pink , purple , someoffset))),1.0);
}
