#define COLOR 1

float luminance(vec3 color) {
    return dot(color, vec3(0.2126, 0.7152, 0.0722));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    vec3 blueNoise = texture( iChannel0, fragCoord/1024.).rgb;
    vec3 image = texture( iChannel1, uv).rgb;

    float n = luminance(blueNoise);
    float g = luminance(image);

    #if COLOR
        fragColor = vec4( step( pow(blueNoise.r, 1./1.7), image ), 1. );
    #else
        fragColor = vec4( step( pow(blueNoise.r, 1./1.7), g ) );
    #endif
}
