renderpass.code:
float sigm(float x) {
    return 1.0 / (1.0 + (exp(-(x - 0.5) * 14.0))); 
}

vec2 toUV(vec2 fragCoord, vec2 iResolution) {
    return (fragCoord - (iResolution.xy / 2.)) / min(iResolution.x, iResolution.y);
}

float ang_vel = 1.;
float obj1mass = .1;
float obj2mass = .05;
float obj2dist = .4;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 obj2pos = vec2(cos(iTime), sin(iTime)) * obj2dist;
    vec2 obj1pos = -obj2pos * obj2mass / obj1mass;

    vec2 uv = toUV(fragCoord.xy, iResolution.xy);

    vec2 obj1dir = (obj1pos - uv);
    vec2 obj2dir = (obj2pos - uv);

    if (length(obj1dir) < .05) {
        fragColor = vec4(1., 1., 0., 0.);
        return;
    }

    if (length(obj2dir) < .05) {
        fragColor = vec4(0., 1., 0., 0.);
        return;
    }

    if (abs(length(uv) - .03) < .002) {
        fragColor = vec4(1.);
        return;
    }

    vec2 accel1 = normalize(obj1dir) * obj1mass / length(obj1dir) / length(obj1dir);
    vec2 accel2 = normalize(obj2dir) * obj2mass / length(obj2dir) / length(obj2dir);
    vec2 total_accel = accel1 + accel2;
    vec2 ideal_accel = normalize(-uv) * length(uv) * ang_vel * ang_vel;
    float accel_diff = length(total_accel - ideal_accel);

    fragColor = vec4(1. - accel_diff * 30., 0., 0., 0.);
}
