float halfWidth = 0.05f;
float crossPoint = 0.3;
float circlePoint = 0.5;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy)/iResolution.y;

    uv = abs(uv);
    float circleDist = abs(length(circlePoint - uv) + crossPoint - circlePoint);
    float crossDist = min(abs(crossPoint - uv.x), abs(crossPoint - uv.y));
    float value = (max(uv.x, uv.y) < circlePoint) ? crossDist : circleDist;

    value = step(value, halfWidth);
    fragColor = vec4(vec3(value),1.0);
}
