void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.0 - 1.0;

    //uv = fract(abs(sin(uv*1.5)))-0.5;

    float x = sin(cos((uv.x*uv.y+iTime/10.0)*20.0)*3.0+iTime);
    float y = abs(sin(cos((uv.x*uv.y/(0.9+sin(iTime*0.1))))*3.0+iTime*2.0));

    vec3 a = vec3(0.8, 0.3, 0.7);
    vec3 b = vec3(0.3, 0.4, 0.9);

    float v = x+y;
    if (v>1.0) v = 1.0;
    if (v<0.0) v = 0.0;

    fragColor = vec4(v*a+(1.0-v)*b,1.0);
}
