void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Вычисление индекса квадратика
    vec2 squareCoord = floor(fragCoord / 16.0);

    // Перемешивание квадратиков (можно изменять алгоритм перемешивания)
    float tempX = sin(squareCoord.x * 43758.5453 + squareCoord.y * 76915.4234) * 77777.8787;
    float tempY = sin(squareCoord.x * 43758.5453 + squareCoord.y * 76915.4234) * 77777.8787;

    // Установка новых координат для пикселя
    vec2 newCoord = fragCoord + vec2(tempX, tempY);

    // Получение цвета из новых координат
    vec4 color = texture(iChannel0, newCoord / iResolution.xy);

    fragColor = color;
}
