/*
    Mixbox Pigment Paint Mixing by chronos

    -------------------------------------------------------------

    I tried to make use of the Mixbox library.
    Unfortunately it includes a LUT texture that I cannot easily import to shadertoy,
    so I just made my own by linearly interpolating the corners.
    Unfortunately, that's not a very good approximation, so it is way off.
    But at least it kindof works, so now it should just be a matter of improving the hardcoded LUT :)


    https://scrtwpns.com/mixbox/
    https://github.com/scrtwpns/mixbox/tree/master/shaders
    https://github.com/scrtwpns/mixbox/blob/master/shaders/mixbox_lut.png

    -------------------------------------------------------------

    IMPORTANT NOTE!

    This shader includes the "mixbox.glsl" shader code
    which is under its own license (Creative Commons Attribution-NonCommercial 4.0), 
    which is included along with the code in Buffer D.

    The original authors (Sarka Sochorova and Ondrej Jamriska) are credited,
    there is an indication that the code has been modified,
    and the code is not used for commercial purposes.


    -------------------------------------------------------------



    Please excuse the poor code quality (mine, not theirs), it was just hastily thrown together :)

*/


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 mouse = (2.*iMouse.xy-iResolution.xy)/iResolution.y;

    float mouse_down = float(iMouse.z > 0.);

    vec3 color = vec3(0.075);

    if(uv.x < 1.35)
    {
        color = texture(iChannel1, fragCoord/iResolution.xy).rgb;
    }

    for(float i = 0.; i < 7.; i++)
    {
        float y = 0.8-(3.5*i)/13.;
        float x = 1.5;
        float r = 0.075;
        color = mix(color, vec3(0.01), smoothstep(0.01, 0., length(uv-vec2(x,y))-r*1.1));

        if(texture(iChannel2, vec2(0)).a == i)
            color = mix(color, vec3(1), smoothstep(0.01, 0., length(uv-vec2(x,y))-r));

        color = mix(color, pigments[int(i)].linRGB, smoothstep(0.01, 0., length(uv-vec2(x,y))-r*0.9));
    }

    for(float i = 0.; i < 6.; i++)
    {
        float y = 0.65-(3.5*i)/13.;
        float x = 1.675;
        float r = 0.075;
        color = mix(color, vec3(0.01), smoothstep(0.01, 0., length(uv-vec2(x,y))-r*1.1));

        if(texture(iChannel2, vec2(0)).a == i+7.)
            color = mix(color, vec3(1), smoothstep(0.01, 0., length(uv-vec2(x,y))-r));

        color = mix(color, pigments[int(i+7.)].linRGB, smoothstep(0.01, 0., length(uv-vec2(x,y))-r*0.9));
    }


    color = sRGBencode(color);
    fragColor = vec4(color,1.0);
}

struct Pigment
{
    vec3 sRGB256;
    vec3 sRGB;
    vec3 linRGB;
};

// Pigment		                               RGB	             Float RGB	                           Linear RGB
const Pigment Cadmium_Yellow		 = Pigment(vec3(254, 236, 0)	, vec3(0.996, 0.925, 0.0)	      , vec3(0.991, 0.839, 0.0));
const Pigment Hansa_Yellow		 = Pigment(vec3(252, 211, 0)	, vec3(0.988, 0.827, 0.0)	      , vec3(0.973, 0.651, 0.0));
const Pigment Cadmium_Orange		 = Pigment(vec3(255, 105, 0)	, vec3(1.0, 0.412, 0.0)	      , vec3(1.0, 0.141, 0.0));
const Pigment Cadmium_Red		     = Pigment(vec3(255, 39, 2)	, vec3(1.0, 0.153, 0.008)	      , vec3(1.0, 0.02, 0.001));
const Pigment Quinacridone_Magenta = Pigment(vec3(128, 2, 46)	, vec3(0.502, 0.008, 0.18)      , vec3(0.216, 0.001, 0.027));
const Pigment Cobalt_Violet		 = Pigment(vec3(78, 0, 66)	    , vec3(0.306, 0.0, 0.259)	      , vec3(0.076, 0.0, 0.054));
const Pigment Ultramarine_Blue	 = Pigment(vec3(25, 0, 89)	    , vec3(0.098, 0.0, 0.349)	      , vec3(0.01, 0.0, 0.1));
const Pigment Cobalt_Blue		     = Pigment(vec3(0, 33, 133)	, vec3(0.0, 0.129, 0.522)	      , vec3(0.0, 0.015, 0.235));
const Pigment Phthalo_Blue		 = Pigment(vec3(13, 27, 68)	, vec3(0.051, 0.106, 0.267)     , vec3(0.004, 0.011, 0.058));
const Pigment Phthalo_Green		 = Pigment(vec3(0, 60, 50)	    , vec3(0.0, 0.235, 0.196)	      , vec3(0.0, 0.045, 0.032));
const Pigment Permanent_Green		 = Pigment(vec3(7, 109, 22)	, vec3(0.027, 0.427, 0.086)	  , vec3(0.002, 0.153, 0.008));
const Pigment Sap_Green		     = Pigment(vec3(107, 148, 4)	, vec3(0.42, 0.58, 0.016)	      , vec3(0.147, 0.296, 0.001));
const Pigment Burnt_Sienna		 = Pigment(vec3(123, 72, 0)	, vec3(0.482, 0.282, 0.0)	      , vec3(0.198, 0.065, 0.0));


const Pigment pigments[13] = Pigment[13](
    Cadmium_Yellow,
    Hansa_Yellow,

    Cadmium_Orange,
    Cadmium_Red,

    Quinacridone_Magenta,
    Cobalt_Violet,

    Ultramarine_Blue,
    Cobalt_Blue,
    Phthalo_Blue,

    Phthalo_Green,
    Permanent_Green,
    Sap_Green,

    Burnt_Sienna
);

float sRGBencode(float C_linear) { return C_linear > 0.0031308 ? (1.055 * pow(C_linear, 1./2.4) - 0.055) : (12.92 * C_linear); }
vec3 sRGBencode(vec3 C_linear) { C_linear = clamp(C_linear, 0., 1.); return vec3(sRGBencode(C_linear.x), sRGBencode(C_linear.y), sRGBencode(C_linear.z)); }
float sRGBdecode(float C_sRGB) { return C_sRGB > 0.04045 ? (pow((C_sRGB + 0.055)/1.055, 2.4) ) : (C_sRGB / 12.92); }
vec3 sRGBdecode(vec3 C_sRGB) { return vec3(sRGBdecode(C_sRGB.x), sRGBdecode(C_sRGB.y), sRGBdecode(C_sRGB.z)); }

vec3 from_hex(int hex)
{
    return sRGBdecode(vec3( (hex >> 16)&0xFF, (hex >> 8)&0xFF, hex & 0xFF)/255.);
}

vec3 replacement_lut(vec2 uv)
{
    uv = clamp(uv, 0., 1.);
    uv.x = 1.-uv.x;
    vec3 uvw = vec3(fract(uv * 8.), (floor(uv.x*8.) + floor(uv.y*8.)*8.) / 64.);

    vec3 bot_left_start  = from_hex(0x538D00);
    vec3 top_left_start  = from_hex(0x7C433F);
    vec3 bot_right_start = from_hex(0x01D300);
    vec3 top_right_start = from_hex(0x003EBF);
    vec3 bot_left_end    = from_hex(0x6E0900);
    vec3 top_left_end    = from_hex(0xB0000F);
    vec3 bot_right_end   = from_hex(0x000000);
    vec3 top_right_end   = from_hex(0x1B005F);

    uvw.xz = 1.-uvw.xz;

    vec3 bot_start = mix(bot_left_start, bot_right_start, uvw.x);
    vec3 top_start = mix(top_left_start, top_right_start, uvw.x);

    vec3 bot_end = mix(bot_left_end, bot_right_end, uvw.x);
    vec3 top_end = mix(top_left_end, top_right_end, uvw.x);

    vec3 start = mix(bot_start, top_start, uvw.y);
    vec3 end = mix(bot_end, top_end, uvw.y);

    vec3 final = mix(start, end, uvw.z);

    final = pow(final, vec3(1./1.2)); // fudge it a bit, idk xD

    return final;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame == 0) fragColor = vec4(1,0,0,1);
    else fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);

    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 mouse = (2.*iMouse.xy-iResolution.xy)/iResolution.y;
    float mouse_down = float(iMouse.z > 0.);
    if(mouse_down>0. && (length(mouse-vec2(1.6,0.5))-0.1) < 0.) fragColor = vec4(1,0,0,1);
    if(mouse_down>0. && (length(mouse-vec2(1.6,0))-0.1) < 0.) fragColor = vec4(0,1,0,1);
    if(mouse_down>0. && (length(mouse-vec2(1.6,-0.5))-0.1) < 0.)  fragColor = vec4(0,0,1,1);


    for(float i = 0.; i < 7.; i++)
    {
        float y = 0.8-(3.5*i)/13.;
        float x = 1.5;
        float r = 0.075;

        if( mouse_down > 0. && (length(mouse-vec2(x,y)) < r) )
            fragColor.a = i;
    }

    for(float i = 0.; i < 6.; i++)
    {
        float y = 0.65-(3.5*i)/13.;
        float x = 1.675;
        float r = 0.075;
        if( mouse_down > 0. && (length(mouse-vec2(x,y)) < r) )
            fragColor.a = i+7.;
    }

}

