void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   if(iFrame == 0) { 
        float canvas_grain = -0.05+0.025*texture(iChannel0, fragCoord/iResolution.xy).r * texture(iChannel3, fragCoord/iResolution.xy).r;
        fragColor = vec4(vec3(0.75 + canvas_grain), 1);
        return;
   }

    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 mouse = (2.*iMouse.xy-iResolution.xy)/iResolution.y;

    float mouse_down = float(iMouse.z > 0.);

    vec3 color = texelFetch(iChannel2, ivec2(fragCoord), 0).rgb;

    vec4 paint = texelFetch(iChannel1, ivec2(fragCoord), 0).rgba;

    const float PI = 3.14159265;

    float rcp_std = 14. * PI;
    //float c = cos(sin(iTime/8.)), s = sin(sin(iTime/8.));
    float c = cos(iTime/8.), s = sin(iTime/8.);
    mat2 R = mat2(c,s, -s, c);
    float brush = exp(-.5 * rcp_std * length(mouse-uv)) * texture(iChannel3, 0.5 + (uv-mouse)).r * texture(iChannel3, 0.5 + R*(uv-mouse) + 0.3 * vec2(c,s)).r;

    vec3 rgb = 
        Cadmium_Red.linRGB * paint.r +
        Hansa_Yellow.linRGB * paint.g +
        Cobalt_Blue.linRGB * paint.b;

    rgb = pigments[int(paint.a)].linRGB;

    float t = 0.5 * brush * mouse_down;

    color = mixbox_lerp(color, rgb, t);

    fragColor = vec4(color,1.0);
}
