void mainImage( out vec4 pxColor, in vec2 pxCoord ) {
    float whMin = min(iResolution.x, iResolution.y);
    vec2 uv = pxCoord / whMin;
    uv.x -= abs(iResolution.x - iResolution.y) / whMin / 2.0;
    uv -= vec2(0.5, 0.5);
    uv *= 2.0;

    bool isRed = distance(uv, vec2(0.0, 0.0)) < 0.05;
    bool isGrn = distance(uv, vec2(0.0, 1.0)) < 0.05;
    bool isBlu = distance(uv, vec2(1.0, 0.0)) < 0.05;

    pxColor = vec4(isRed, isGrn, isBlu, 1.0);
}
