// freq:frequence
//amp: amplitude 
// octaves

//turbulence
T Turbulence(in vec3 p, float freq, float amp, int octaves)
{
    float v = 0.0;
    float amplitudeT = 0.0; // Amplitude totale

    for (int i = 0; i < octaves; ++i) {
        v = v + Noise(p) * amp;
        p = p * freq;
        amp = amp * 0.5;
        amplitudeT = amplitudeT + amp;
    }


    return T(vec3(v), 0.0);
}




