//tetxure marbre sans veine avc turbulence

T TextMarbreSV(in vec3 p, float freq, float amp, int oct)
{

return Turbulence(p,freq,amp,oct);

}


//texture marbre avec veines
//param:

T TextmarbreV(in vec3 p, float freq, float amp, int oct,float Vfreq, float Vamp)
{
T marbre=Turbulence(p,freq,amp,oct);
T veines=Turbulence(p,Vfreq, Vamp, oct);
// Addition des couleurs des textures Individuellement 
  vec3 colorSum = 0.5 * (marbre.c + veines.c);


    float specular = marbre.s; // La spécularité est prise à partir de une des textures, ici c'est marbre


    T resultat = T(colorSum, specular); // la texture résultat

    return resultat;

}

//texture bois:

// texture de bois avec nœuds
T TextureBois(in vec3 p)
{
    // Bruit de Perlin
    float wn = Noise(p * 0.5);


    float vd = Voronoi(p).x; // ajout de nœuds avec Voronoi


    float fac = smoothstep(0.2, 0.8, vd); //facteur de distorsion

    // Couleur 
    vec3 color = mix(vec3(0.4, 0.2, 0.5), vec3(0.0, 0.4, 0.2), wn);

    //  spécularité
    float spi = 0.5;

    // on cree la texture
    T wtexture;
    wtexture.c = color;
    wtexture.s = spi;


  wtexture.c = mix(wtexture.c, vec3(0.55),fac); //mix noeuds
   wtexture.s *= (1.0 - fac);

    return wtexture;
}







