float Line(vec2 uv, float x1, float x2, float y1, float y2) {
    float c = 0.;
    if((uv.x > x1) && (uv.x < x2) && (uv.y > y1) && (uv.y < y2)){
        c = 1.;
    }
    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = vec4(0.0, 0.0, 0.0, 0.0);

    fragColor += Line(uv, 0.124, 0.128, 0.05, 0.95);
    //fragColor += Line(uv, 0.274, 0.276, 0.0, 1.0);
    //fragColor += Line(uv, 0.424, 0.426, 0.0, 1.0);
    //fragColor += Line(uv, 0.574, 0.576, 0.0, 1.0);
    //fragColor += Line(uv, 0.724, 0.726, 0.0, 1.0);
    fragColor += Line(uv, 0.874, 0.878, 0.05, 0.95);

    // horizontal lines
    fragColor += Line(uv, 0.125, 0.425, 0.05, 0.07);
    fragColor += Line(uv, 0.575, 0.875, 0.05, 0.07);

    fragColor += Line(uv, 0.275, 0.725, 0.2, 0.205);

    fragColor += Line(uv, 0.125, 0.275, 0.4, 0.405);
    fragColor += Line(uv, 0.425, 0.575, 0.4, 0.405);

    fragColor += Line(uv, 0.425, 0.725, 0.6, 0.605);

    fragColor += Line(uv, 0.125, 0.425, 0.8, 0.805);
    fragColor += Line(uv, 0.575, 0.725, 0.8, 0.805);

    fragColor += Line(uv, 0.125, 0.425, 0.93, 0.95);
    fragColor += Line(uv, 0.575, 0.875, 0.93, 0.95);

    // vertical lines
    fragColor += Line(uv, 0.575, 0.577, 0.05, 0.2);

    fragColor += Line(uv, 0.425, 0.427, 0.2, 0.4);
    fragColor += Line(uv, 0.725, 0.727, 0.2, 0.4);

    fragColor += Line(uv, 0.275, 0.277, 0.4, 0.6);

    fragColor += Line(uv, 0.425, 0.427, 0.6, 0.8);

    fragColor += Line(uv, 0.575, 0.577, 0.8, 0.95);
}
