void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = fragCoord / iResolution.yy;
    vec2 q = p - vec2(iResolution.x / iResolution.y / 2.0, 0.5);

    vec3 color = mix(vec3(1.0, 2.0, 0.3), vec3(0.5, 0.9, 1.0), p.y);

    float distMidY = 1.0 - smoothstep(0.0, 0.3, abs(q.y));
    color += vec3(distMidY / 1.5, 0.0, 0.0);

    float sunshine = 0.6 * (1.0 - smoothstep(0.0, 0.8, length(q)));
    vec3 shine = vec3(sunshine, sunshine, sunshine / 2.0);

    float sunny = 0.04 * (1.0 - smoothstep(0.15, 0.22, length(q)));
    vec3 sun = vec3(sunny, sunny, sunny);

    color += shine + sun;
    color += 0.2 * smoothstep(0.5, 2.0, length(q) + cos(10.0 * atan(q.y/q.x)) );

    fragColor = vec4(color, 1.0);


}
