#define PI 3.14159265359
#define RADIUS .3
#define HEIGHT 1.
#define ANGLE -20.

#define GRID_SIZE_X 48.
#define GRID_SIZE_Y 48.

float get_halftone(vec2 uv, vec2 p)
{
    p *= vec2(
        GRID_SIZE_X,
        GRID_SIZE_Y
    );
    p.x += iTime*1.5;

    float m = length(fract(p)-.5)-RADIUS*(uv.y*10.-HEIGHT);
    m = smoothstep(.2,.25,m);
    return 1.-m;
}

vec2 rotate(vec2 v, float t)
{
    t = t*PI/180.;
    return mat2(
        cos(t), -sin(t),
        sin(t),  cos(t)
    )*v;
}

vec4 get_ui(vec2 U)
{
    vec2 ires = iResolution.xy;
    vec2 uv = (U-.5*ires.xy)/ires.y;
    vec2 suv = U/ires.xy;
    vec2 ruv = rotate(suv-.5, ANGLE);

    float m = get_halftone(ruv, uv);
    vec4 col = texture(iChannel0, suv);

    // Shadow
    col *= smoothstep(0.17, -.05, ruv.y);

    // Halftone
    col = mix(col, vec4(1., .2, .4, 1.), m);

    // BG strip
    col = mix(col, vec4(1., .25, .45, 1.), smoothstep(0.16, .161, ruv.y));

    // BG
    col = mix(col, vec4(vec3(.13), 1.), smoothstep(0.21, .211, ruv.y));

    return col;
}

void mainImage(out vec4 O, in vec2 U)
{
    O = get_ui(U);
}
