const float TAU = 6.28318530717959;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 iRes = iResolution.xy;
    float time = iTime;

    vec2 center = iRes * 0.5;
    vec2 uv = center - fragCoord;
    float a = fract(atan(uv.y, uv.x) / TAU);
    float p = (cos(a * TAU * 7.0 + time) + 1.0) / 2.0;
    float c = pow(clamp((cos(((length(uv) + time * -100.0) - mix(200.0, 300.0, pow(p, 1.0))) * 0.02) + 1.0) / 2.0, 0.0, 1.0), 0.5);

    fragColor = vec4(
        0.1 + (0.7 * (pow((cos(a * TAU * 7.0 + time * 1.0) + 1.0), 0.3)) / 2.0) * c,
        0.01,
        clamp((sin(length(uv) * 0.01 - time * 4.0) + 1.0) / 2.0, 0.0, 1.0) * 1.0 - c,
        1.0
    );
}
