const float epsilon = 0.0001;

void mainImage( out vec4 O, in vec2 U )
{    
    vec2  a   = normalize(vec2(0) + min(0.0, iTime));

    float nan = any(isnan(a)) ? 1.0 : 0.0;
    float inf = any(isinf(a)) ? 1.0 : 0.0;
    float zro = abs(length(a))       < epsilon ? 1.0 : 0.0;
    float one = abs(length(a)) - 1.0 < epsilon ? 1.0 : 0.0;;

    vec2  uv  = U/iResolution.xy;

    float c;
    if (uv.x < 1.0/4.0) {
        c = nan;
    }
    else if (uv.x < 2.0/4.0) {
        c = inf;
    }
    else if (uv.x < 3.0/4.0) {
        c = zro;
    }
    else {
        c = one;
    }

    c = c * 0.5;

    O = vec4(c, c, c, 1);

    int i = int(U.x);
    if (i % int(iResolution.x / 4.0) == 0 || i == int(iResolution.x) - 1) {
        O = vec4(1);
    }
}
